package day7.com;

public class Patternex1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(int i=10;i>=1;i--)//Row
     {
    	 for(int j=1;j<=i;j++)// column
    	 {
    		 //System.out.println("i="+i+"j="+j);
    		 System.out.print("* ");

    	 }
    	 System.out.println();
     }
	}

}
